import React, { useState, useEffect } from 'react';
import logo from '../../assets/logo.svg';
import './styles.css';
import { Link } from 'react-router-dom';
import { FiArrowLeft } from 'react-icons/fi';
import { Map, TileLayer, Marker } from 'react-leaflet';
import api from '../../services/api';
import axios from 'axios';
interface Item {
  id: number;
  title: string;
  image_url: string;
}
interface UF {
  id: number;
  sigla: string;
  nome: string;
}
interface City {
  id: number;
  nome: string;
}
const CreatePoint: React.FC = () => {
  const [items, setItems] = useState<Item[]>([]);
  const [ufs, setUfs] = useState<UF[]>([]);
  const [ufSelected, setUfSelected] = useState<string>();
  const [cities, setCities] = useState<City[]>([]);

  useEffect(() => {
    axios.get('/items').then(({ data }) => {
      setItems(data);
    });
  }, []);
  useEffect(() => {
    api
      .get('https://servicodados.ibge.gov.br/api/v1/localidades/estados')
      .then(({ data }) => {
        setUfs(data);
      });
  }, []);
  useEffect(() => {
    api
      .get(
        `https://servicodados.ibge.gov.br/api/v1/localidades/estados/${ufSelected}/municipios`
      )
      .then(({ data }) => {
        setCities(data);
      });
  }, [ufSelected]);

  return (
    <div id='page-create-point'>
      <header>
        <img src={logo} alt='Ecoleta' />
        <Link to='/'>
          <FiArrowLeft />
          Volta para home
        </Link>
      </header>
      <form>
        <h1>
          Cadastrdo do <br />
          ponto de Coleta
        </h1>
        <fieldset>
          <legend>
            <h2>Dados</h2>
          </legend>
          <div className='field'>
            <label htmlFor='name'> Nome da entidade</label>
            <input type='text' name='name' id='name' />
          </div>
          <div className='field-group'>
            <div className='field'>
              <label htmlFor='email'> E-mail</label>
              <input type='email' name='email' id='email' />
            </div>
            <div className='field'>
              <label htmlFor='WhatsApp'> WhatsApp</label>
              <input type='text' name='WhatsApp' id='WhatsApp' />
            </div>
          </div>
        </fieldset>
        <fieldset>
          <legend>
            <h2>Endereços</h2>
            <span>Selecione um endereço no mapa</span>
          </legend>

          <Map center={[-23.4968275, -46.8745015]} zoom={15}>
            <TileLayer
              attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
            />
            <Marker position={[-23.4968275, -46.8745015]} />
          </Map>

          <div className='field-group'>
            <div className='field'>
              <label htmlFor='uf'> Estado (UF)</label>
              <select
                value={ufSelected}
                onChange={(e) => setUfSelected(e.target.value)}
                name='uf'
                id='uf'
              >
                <option value='0'>Selecione uma UF</option>
                {ufs.map((uf) => (
                  <option key={uf.id} value={uf.sigla}>
                    {uf.nome} - {uf.sigla}
                  </option>
                ))}
              </select>
            </div>
            <div className='field'>
              <label htmlFor='city'> Cidade</label>
              <select name='city' id='city'>
                <option value='0'>Selecione uma Cidade</option>
              </select>
            </div>
          </div>
        </fieldset>
        <fieldset>
          <legend>
            <h2>Ítems de coleta</h2>
            <span>Selecione um ou mais ítems abaixo</span>
          </legend>
          <ul className='items-grid'>
            {items.map((item) => (
              <li key={item.id}>
                <img src={item.image_url} alt={item.title} />
                <span>{item.title}</span>
              </li>
            ))}
          </ul>
        </fieldset>
        <button type='submit'>Cadastrar ponto de Coleta</button>
      </form>
    </div>
  );
};

export default CreatePoint;
